<?php get_header(); ?>	<div id="main">	<div class="section entry" id="entry<?php the_ID(); ?>">		<?php if(have_posts()) : ?><?php while(have_posts()) : the_post(); ?>		<div <?php post_class(); ?>>		<div class="post">			<h2 class="entry-title"><a href="<?php the_permalink() ?>"  title="<?php the_title_attribute() ; ?>" rel="bookmark"><?php the_title_attribute(); ?></a></h2>				<p class="info">					<span class="dateinfo"><a href="<?php the_permalink() ?>"><?php the_time( get_option( 'date_format' )); ?></a></span>					<span class="catinfo"><?php echo __('Category :', 'mxs'); the_category('| ') ?><?php edit_post_link(__(' Edit', 'mxs'), ' &#124; ', ''); ?></span>					<span class="cmtinfo"><?php comments_popup_link('0', '1', '%','',__('Off', 'mxs')); ?></span>				</p><div class="clear"></div>			<div class="indexentry">					<?php if ( has_post_thumbnail() ){ ?>					<div class="thumb"><a href="<?php the_permalink() ?>">					<?php the_post_thumbnail(array(360,130)); ?></a>				 </div>				<?php } $mxs_options = get_option('mxs_theme_options'); ?>					<?php if ( $mxs_options['home_excerpt_check']== 1 ) { the_excerpt(); } else {the_content(__('&raquo; Continue Reading','mxs'));} ?>			</div>			<div class="clear"></div>			<?php wp_link_pages(); ?>		</div></div>			<?php endwhile; ?>		<?php else : ?>	<div id="main">	 <h2><?php _e('Not Found', 'mxs'); ?></h2>	 <?php get_search_form(); ?>	</div>		<?php endif; ?>	</div><!-- section entry -->	<?php if (  $wp_query->max_num_pages > 1 ) : ?>			<div class="page_navi">			<?php if(function_exists('mxs_pagenavi')) {  mxs_pagenavi(5); } else{ ?>				<?php next_posts_link(); ?>				<?php previous_posts_link(); }?>			</div>	<?php endif;  ?><!-- page navi -->	</div><!-- main -->	<?php	get_sidebar(); ?>	<?php	get_footer(); ?>