<?php get_header(); ?>	<div id="main">	<div class="section entry" id="entry<?php the_ID(); ?>">		<?php if(have_posts()) : ?><?php while(have_posts()) : the_post(); ?>		<?php get_template_part( 'content', get_post_format() ); ?>			<?php endwhile; ?>		<?php else : ?>	<div id="main">	 <h2><?php _e('Not Found', 'MxS'); ?></h2>	 <?php get_search_form(); ?>	</div>		<?php endif; ?>	</div><!-- section entry -->	<?php if (  $wp_query->max_num_pages > 1 ) : ?>			<?php if (function_exists('MxS_pagenavi')) : ?><div class="page_navi"><?php MxS_pagenavi(5); ?></div>		<?php else: ?>			<div id="page_navi">				<?php next_posts_link(); ?>				<?php previous_posts_link(); ?>			</div>		<?php endif;  ?>	<?php endif;  ?><!-- page navi -->	</div><!-- main -->	<?php	get_sidebar(); ?>	<?php	get_footer(); ?>