<?php get_header(); ?>	<div id="main">		<ul id="flip1">		<li class="newer"><?php previous_post_link('%link', __('&laquo; Previous', 'MxS')); ?> </li>		<li class="older">|<?php next_post_link('%link', __('Next &raquo; ', 'MxS')); ?> </li>		</ul>
	<div class="section entry" id="entry<?php the_ID(); ?>" <?php post_class(); ?>>	<?php while(have_posts()) : the_post(); ?>		<div class="post">			<h1 class="entry-title"><?php the_title_attribute(); ?></h1>				<div class="singleinfo">				<span class="dateinfo"><?php _e('Posted on ', 'MxS'); the_time( get_option( 'date_format' )); ?> | <?php echo __('Category', 'MxS') ,' :'; the_category(', ') ?> | <?php comments_popup_link(); ?></span>				</div><div class="clear"></div>
			<div class="entry"><?php the_content(); ?></div>			<div class="clear"></div>
				<?php wp_link_pages(); ?>
				<span class="infobottom">					 <?php if (has_tag()) { _e('&raquo; Tags:', 'MxS'); the_tags('  <span class="tags">', ' , ', '</span>'); } ?>					 <?php edit_post_link(__(' Edit', 'MxS'), ' &#124; ', ''); ?>				</span>		</div>			<?php endwhile; ?>		<div class="related">			<?php if(function_exists('related_posts')) {  related_posts('number=10&include_page=false&order=count-desc'); } ?>		</div>			<div class="flip">	<div class="prevpost"><?php previous_post_link(); ?></div>	<div class="nextpost"><?php next_post_link(); ?></div>	</div><?php comments_template(); ?>	</div><!-- section entry -->	</div><!-- main -->	<?php	get_sidebar(); ?>		<?php	get_footer(); ?>